    private void botaoListarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_botaoListarActionPerformed
        java.sql.Connection conexao = Conexao.getConexao();
        String comando = "SELECT * FROM contato";
        try{
            //prepara o comando pra ser executado
            java.sql.PreparedStatement ps = conexao.prepareStatement(comando);
            //executa a consulta no bd
            java.sql.ResultSet rs = ps.executeQuery();
            //percorre os resultados e exibe na tela
            lista.setText(null);
            while(rs.next()){
                lista.append(rs.getString("codigo")+"\n");
                lista.append(rs.getString("nome")+"\n");
                lista.append(rs.getString("email")+"\n");
                lista.append(rs.getString("codigo")+"\n");
                lista.append(rs.getString("telefone")+"\n");
            }//while
        } catch(Exception e){
            JOptionPane.showMessageDialog(null,e.getMessage());
        }//catch
    }//GEN-LAST:event_botaoListarActionPerformed

